mapper.select:
select id, 
from order_main
where id=#{id}

select id, orderId, inventoryId, inventoryName, num, price, supplyPrice
from order_detail
where orderId=#{id}

mapper.insert:

    select currval(pg_get_serial_sequence('order_main', 'id'));

insert into order_main()
    values(#{order.consigneeAddress.consignee}, #{order.consigneeAddress.phone}, #{order.consigneeAddress.province}, 
    #{order.consigneeAddress.city}, #{order.consigneeAddress.district}, #{order.consigneeAddress.address},  #{order.orderDate}, 0);

    insert into order_detail(orderId, inventoryId, inventoryName, num, price, supplyPrice)
        values(currval(pg_get_serial_sequence('order_main', 'id')), #{item.inventoryId}, #{item.inventoryName}, #{item.num}, #{item.price}, #{item.supplyPrice});
